import React, { useState, useEffect } from 'react';
import { Table, Input, Select, Pagination } from 'antd';
import axios from 'axios'; // Import axios for API requests
import { useNavigate, useLocation } from 'react-router-dom';

const App = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [pagination, setPagination] = useState({
    currentPage: 1,
    pageSize: 10,
  });
  const [filters, setFilters] = useState({
    tags: [],
    searchText: '',
  });

  const navigate = useNavigate();
  const location = useLocation();

  const getData = async () => {
    setLoading(true);
    try {
      const { search } = location;
      const queryParams = new URLSearchParams(search);
      const currentPage = parseInt(queryParams.get('page') || 1);
      const pageSize = parseInt(queryParams.get('pageSize') || 10);
      const tags = queryParams.getAll('tags');
      const searchText = queryParams.get('searchText') || '';

      const response = await axios.get(`https://dummyjson.com/posts?limit=${pageSize}&skip=${(currentPage - 1) * pageSize}`);
      setPosts(response.data); // Limit data to current page size
      setPagination({ ...pagination, currentPage });
      setFilters({ tags, searchText });
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getData();
  }, [location]);

  const handleTableChange = (pagination, filters) => {
    const newParams = new URLSearchParams({
      page: pagination.current,
      pageSize: pagination.pageSize,
      ...filters, // Spread existing filters
    });
    navigate({ search: newParams.toString() });
  };

  const handleTagChange = (value) => {
    setFilters({ ...filters, tags: value });
  };

  const handleSearchChange = (e) => {
    setFilters({ ...filters, searchText: e.target.value });
  };

  const filteredPosts = posts.filter((post) => {
    const hasTag = filters.tags.length === 0 || filters.tags.some((tag) => post.tags.includes(tag));
    const searchTextMatch = post.body.toLowerCase().includes(filters.searchText.toLowerCase());
    return hasTag && searchTextMatch;
  });

  const columns = [
    { title: 'ID', dataIndex: 'id', key: 'id' },
    { title: 'Title', dataIndex: 'title', key: 'title' },
    { title: 'Body', dataIndex: 'body', key: 'body' },
  ];

  return (
    <div>
      <Input.Search style={{ marginBottom: 16 }} placeholder="Search posts..." onChange={handleSearchChange} />
      <Select
        mode="multiple"
        style={{ width: 200, marginBottom: 16 }}
        placeholder="Filter by tags"
        onChange={handleTagChange}
        value={filters.tags}
      >
        {posts.map((post) => (
          <Select.Option key={post.id} value={post.id}>
            {post.tags.join(', ')}
          </Select.Option>
        ))}
      </Select>
      <Table columns={columns} dataSource={filteredPosts} loading={loading} pagination={pagination} onChange={handleTableChange} />
    </div>
  );
};

export default App;
